import React from "react";
import "../stylesheets/nav.css";

class Nav extends React.Component {
  constructor(props) {
    super(props);
    this.toggleModal = this.toggleModal.bind(this);
    this.state = {
      activeModal: false
    };
  }

  render() {
    return (
      <div>
        <button className="floating-nav" onClick={this.toggleModal} />
        <div id="nav-modal" className="modal">
          <ul>
            <li>Home</li>
            <li>Contact</li>

          </ul>
        </div>
        <p className="floating-name">Campbell Oleson</p>
      </div>
    );
  }

  toggleModal() {
    let modal = document.getElementById("nav-modal");
    this.setState({
      activeModal: !this.state.activeModal
    });

    if (this.state.activeModal) {
      modal.classList.remove("active-modal");
      modal.classList.add("inactive-modal");
    } else {
      modal.classList.add("active-modal");
      modal.classList.remove("inactive-modal");
    }
  }
}

export default Nav;
