import React from "react";
import "../../stylesheets/top-panel.css";

class ScrollIntoView extends React.Component {
  componentDidMount() {
    this.scroll();
  }

  componentDidUpdate() {
    this.scroll();
  }

  scroll() {
    const { id } = this.props;
    if (!id) {
      return;
    }
    const element = document.querySelector(id);
    if (element) {
      element.scrollIntoView();
    }
  }

  render() {
    return this.props.children;
  }
}

const TopPanel = () => {
  return (
    <div id="top-panel-wrapper">
      <div className="vert-center">
        <section>
          <header className="tpt">Hello!</header>
          <main className="tpt">
            I am a full-stack web devloper based in Salt Lake City, Utah
          </main>
          <p className="tpt">
            Lorem ipsum dolor sit amet et delectus accommodare his consul
            copiosae legendos at vix ad putent
          </p>
        </section>
      </div>
    </div>
  );
};

const MatchWithHash = ({ component: Component, ...props }) => (
  <Match
    {...props}
    render={props => (
      <ScrollIntoView id={props.location.hash}>
        <Component {...props} />
      </ScrollIntoView>
    )}
  />
);

export default TopPanel;
